initialization_command_item.script:
mkdir -p /etc/nginx/sites-available && printf 'server {
    listen 80;
    server_name example.com;
    server_tokens on;
    location / {
        proxy_pass http://backend;
    }
}
' > /etc/nginx/sites-available/default

mkdir -p /etc/test && echo -e 'user guest;
worker_threads 2;
verbose_logging on;' > /etc/test/config.conf
